import styled from 'styled-components';

export const StyledBtn = styled.button`
  border: none;
  background-color: #fb3f78;
  padding: 10px 20px;
  color: whitesmoke;
  font-size: 2rem;
  font-weight: bold;

  &:hover {
    background-color: #136ebe;
  }

  &:last-child {
    background-color: #75ec64;
  }
`
