import styled from 'styled-components';

export const Menu = styled.nav`
  ul {
    list-style: none;
    padding: 0;
    display: flex;

    li > a {
      color: green;
    }

    li + li {
      margin-left: 20px;
    }
  }

  @media screen and (max-width: 800px) {
    flex-direction: column;
  }
`
